import React from 'react'
import ReactDOM from 'react-dom'

const course = 'Half Stack application development'
const part1 = 'Fundamentals of React'
const exercises1 = 10
const part2 = 'Using props to pass data'
const exercises2 = 7
const part3 = 'State of a component'
const exercises3 = 14


const Header = (props) => {
    return (
    <div>
        <h1>{props.course}</h1>
    </div>
    )
}

const Content = (props) => {
    return (
    <div>
		<Part part={props.part1name} exercise={props.part1exercises}/>
       	{props.part2name} {props.part2exercises}
        {props.part3name} {props.part3exercises}
    </div>
    )
}

const Total = (props) => {
    return (
    <div>
        <p>Number of exercises {props.total} </p>
    </div>
    )
}

const App = () => {

  return (
    <div>
      <Header course={course}/>
      <Content 
	  	part1name = {part1}
	  	part1exercises = {exercises1}
	  	part2name = {part2}
	  	part2exercises = {exercises2}
	  	part3name = {part3}
	  	part3exercises = {exercises3}
	  />
      <Total total = {exercises1+exercises2+exercises3}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
