import React from 'react'
import ReactDOM from 'react-dom'

const Part = (props) => {
console.log(props)  
return(
    <p>
      {props.part} {props.exercises}
    </p>
  )
}

const Header = (props) => {
    console.log(props)
	return (
	<div>
        <h1>{props.course}</h1>
	</div>
		)
}

const Content = (props) => {
    return (
    <div>
		<Part part={props.name1} exercises={props.exercises1}/>
       	<Part part={props.part2name} exercises={props.part2exercises}/>
        <Part part={props.part3name} exercises={props.part3exercises}/>
    </div>
    )
}

const Total = (props) => {
    return (
    <div>
        <p>Number of exercises {props.total} </p>
    </div>
    )
}

const App = () => {

const course = 'Half Stack application development'

const part1 = {
   name: 'Fundamentals of React',
   exercises: 10
  }

const exercises1 = 10

const part2 = 'Using props to pass data'
const exercises2 = 7
const part3 = 'State of a component'
const exercises3 = 14

  return (
    <div>
      <Header course={course}/>
      <Content
	  	name1 = {part1.name}
	  	exercises1 = {part1.exercises}
	  	part2name = {part2}
	  	part2exercises = {exercises2}
	  	part3name = {part3}
	  	part3exercises = {exercises3}
	  />
      <Total total = {exercises1+exercises2+exercises3}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
