  	const positive = (good/all)*100
	const all = good+neutral+bad
}

const App = () => {
  // save clicks of each button to own state
  return (
    <div>
      <h1>Give Feedback</h1>
        <button onClick={() => setGood(Ratings.good + 1)}>good</button>
        <button onClick={() => setNeutral(Ratings.neutral + 1)}>neutral</button>  
        <button onClick={() => setBad(Ratings.bad + 1)}>bad</button>  
	  <h1>Statistics</h1>
		Good = {Ratings.good} <br/>
		Neutral = {Ratings.neutral} <br/>
		Bad = {Ratings.bad} <br/>
		All = {Statistics.all} <br/>
		Average = {Statistics.average} <br/>
		Positive = {Statistics.positive}%
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
