import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistic = (props) => {
	const good = props.good
	const bad = props.bad
	const neutral = props.neutral
	const all = props.good+props.neutral+props.bad
	const average = ((props.good*1)+(props.bad*-1))/all
  	const positive = (props.good/all)*100

	return(
		<div>
			<p>{props.name} {props.value}</p>
		</div>
	)

}

const Statistics = (props) => {

	if (props.all === 0) {
		return(
		<div>
			<p>
			No feedback given
			</p>
		</div>
		)
	}

	return (
		<div>
			<p>
				Statistic name="Good" value={props.good}
				Statistic name="Neutral" value={props.neatural}
				Statistic name="Bad" value={props.bad}
				Statistic name="All" value={props.all}
				Statistic name="Average" value={props.average}
				Statistic name="Positive" value={props.positive}
			</p>
		</div>
		)
}

const App = () => {
  // save clicks of each button to own state

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)	

  return (
    <div>
      <h1>Give Feedback</h1>
        <button onClick={() => setGood(good + 1)}>good</button>
        <button onClick={() => setNeutral(neutral + 1)}>neutral</button>  
        <button onClick={() => setBad(bad + 1)}>bad</button>  
	  <h1>Statistics</h1>
		<Statistic good={good} bad={bad} neutral={neutral}/>
    </div>
  )
}

