import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistic = (props) => {
	const good = props.good
	const bad = props.bad
	const neutral = props.neutral
	const all = props.good+props.neutral+props.bad
	const average = ((props.good*1)+(props.bad*-1))/all
  	const positive = (props.good/all)*100

	return (
		<div>
			<p>
			{props.name} {props.result}
			</p>
		</div>
	)

}

const App = () => {
  // save clicks of each button to own state

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)	

  return (
    <div>
      <h1>Give Feedback</h1>
        <button onClick={() => setGood(good + 1)}>good</button>
        <button onClick={() => setNeutral(neutral + 1)}>neutral</button>  
        <button onClick={() => setBad(bad + 1)}>bad</button>  
	  <h1>Statistics</h1>
		<div>
				Statistic name="Good" value={good} <br/>
				Statistic name="Neutral" value={neutral} <br/>
				Statistic name="Bad" value={bad} <br/>
				Statistic name="All" value={all} <br/>
				Statistic name="Average" value={average} <br/>
				Statistic name="Positive" value={positive} <br/>
		</div>
    </div>
  )
}

ReactDOM.render(<App />, 
document.getElementById('root')
)


