import React from 'react'
import ReactDOM from 'react-dom'

const Course = (props) => {
    console.log(props)
	return (
	<div>
        <h1>{props.course}</h1>
	</div>
		)
}


const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  course.parts.map(partName => <li>{part.name}</li>)
  course.parts.map(partExercices => <li>{part.exercises}</li>)

  return (
    <div>
      <Course
      course={course.name}
      partName={partName}
      partExercices={partExercices}
      />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
